mapper.update:
update member set 

	member_email = #{member_email},
	member_code_add = #{member_code_add},
	member_load_add = #{member_load_add},
	member_detail_add = #{member_detail_add}
	
		, member_pw = #{member_pw}
	


	member_pw = #{member_pw} 


	member_point = #{member_point}



	member_coupon = #{member_coupon}


where member_id = #{member_id}
