mapper.select:
		select * from question
		
			where
		
		
			question_category = #{category}
		
		
			and question_check = #{check}
		
		
			question_check = #{check}
		
		
			
				and 
				${kind} like '%${search}%'
			
		
		
			
				 ${kind} like '%${search}%'
			 
		
		order by question_date asc
		
    		offset ${page} rows fetch first 10 rows only
